import SwiftUI
import Drapeau
import Styles
import Faisceau


struct CourseDetailPage: View {
    
    // MARK: Attributs
    
    @Environment(AppEnvironment.self) var appEnvironment
    @EnvironmentObject var metrics: ScreenMetrics
    
    var course: CourseVM
    
    
    // MARK: Init
    
    init(course: CourseVM) {
        self.course = course
        
        let montserratSmall = UIFont(name: "Montserrat-Medium", size: 17) ?? UIFont.systemFont(ofSize: 15)
        UINavigationBar.appearance().titleTextAttributes = [.font: montserratSmall]
    }
    
    
    
    // MARK: View
    
    var body: some View {
        NavigationStack {
            ZStack {
                Color.drapPrimaryBackground
                    .ignoresSafeArea()
                
                content
                    .padding(24)
                    .ignoresSafeArea(edges: .bottom)
            }
            .navigationTitle("Mon Cours")
            .navigationBarTitleDisplayMode(.inline)
        }
        .onAppear {
            print(Date(timeIntervalSince1970: course.date.timeIntervalSince1970
                       + Double(course.signatureClosingDelay * 60)))
        }
    }
    
    
    var content: some View {
        let cornerRadius = (metrics.borderRadius - 24) / 2
        
        return VStack {
            CourseCard(infos: CourseViewInfos(
                name: course.name,
                status: course.status(userId: appEnvironment.student?.id ?? UUID()),
                startDate: course.date,
                endDate: course.endDate,
                place: course.classrooms?.map(\.name).joined(separator: ", ") ?? "Visio",
                signatureEndDate: Date(timeIntervalSince1970: course.date.timeIntervalSince1970 + Double(course.signatureClosingDelay * 60)),
                hasMessages: false,
                signature: course.mySignature(userId: appEnvironment.student?.id ?? UUID())?.image)
            )
            .activeCourseCardShadow()
            
            Spacer()
            
            VStack(spacing: 16) {
                DrapButton(icon: "signature", title: "Valider ma pr√©sence") {
                    print()
                }
                .drapButtonExpand()
            }
            .padding([.bottom, .horizontal], cornerRadius)
        }
    }
}





